import React, { Component } from "react";
require("./unittype");
var unit = require("../src/unit.json");
var APIcall = require('./unitConverCallAPI') 

var keys = Object.keys(unit);
var unitkey;

class secoundInput extends Component {
  constructor(props) {
    super(props);
    this.state = {
       type: props.unit,
       firstUnit : "",
       secoundUnit : "",
       value : 0
     };
  }
  componentWillReceiveProps(props) {
    this.setState({ type: props.unit });
  }

  getfirstUnit = event => {
    this.setState({ firstUnit: event.target.value });
  };

  getsecoundUnit = event => {
    this.setState({ secoundUnit: event.target.value });
    console.log(this.state.secoundUnit);

    let unitObject = {
        type: this.state.type,
       firstUnit : this.state.firstUnit,
       secoundUnit : this.state.secoundUnit,
       value : this.state.value
    }
    APIcall.getValueConverted(unitObject)

  };

  getValue = event => {
    this.setState({ value: event.target.value });
  };

  render() {
    var type = this.state.type;
    var a = " ";
    for (var i = 0; i <= keys.length; i++) {
      if (type == i) {
        unitkey = Object.keys(unit[keys[i]]);
      }
    }

    return (
      <div>
        <div>
        <label id="userInput" onChange={this.getValue}> Enter Value :</label>
        <input
          pattern="[0-9]"
          title="Enther Only Numeric Value"
          type="text"
          id="output"
        />

        <select id="firstUnit"  onChange={this.getfirstUnit} >
          <option value="N/A">Select Unit </option>
          <option>{unitkey[0] || a} </option>
          <option>{unitkey[1] || a}</option>
          <option>{unitkey[2] || a}</option>
          <option>{unitkey[3] || a}</option>
        </select>
        </div>
        <div>
        <label id="userInput"> Enter Value :</label>
        <input
          pattern="[0-9]"
          title="Enther Only Numeric Value"
          type="text"
          id="output"
        />
        <select id="secoundUnit" onChange={this.getsecoundUnit}>
          <option value="N/A">Select Unit </option>
          <option>{unitkey[0] || a} </option>
          <option>{unitkey[1] || a}</option>
          <option>{unitkey[2] || a}</option>
          <option>{unitkey[3] || a}</option>
        </select>
        </div>
      </div>
    );
  }
}

export default secoundInput;
