      FILE: "",
      // IMAGEPATH: this.props.value.IMAGEURL,
      COUNT: "",
      BOOKDATA: [],
      FACKNAM:"abc",
      WISHLISTTITLE : ""
    };
  }

  wishListClick(event){
    this.setState({FACKNAM: event.target.value})

  }

  AddtoBag(event){

  }

