  render() {
    console.log("add widh list",this.props.value.IMAGEURL);

    return (

      <div>
        <Card
          style={{ maxWidth: 180, height: 290, marginLeft: 90, marginTop: 50 }}
        >
          <CardActionArea>
            <Tooltip title={this.state.DESCRIPTION} arrow>


              <div className = "xyz" >
               <img className ="imagePath" src = {this.props.value.IMAGEURL}style={{height:"150px", width:"70%",marginTop:"6%",marginLeft:"15%"}} alt ="no Cover" />
               </div>

            </Tooltip>
            <CardContent>
              <Typography
                style={{
                  fontFamily: "Times New Roman",
                  color: "black",
                  marginTop: -10,
                  fontSize: 13,
                  marginLeft: 10
                }}
              >
                {this.state.TITLE}
              </Typography>
            </CardContent>
            <CardContent>
              <Typography
                style={{
                  fontFamily: "Times New Roman",
                  color: "grey",
                  marginTop: -30,
                  fontSize: 10,
                  marginLeft: 10
                }}
              >
                {this.state.AUTHOR}
              </Typography>
            </CardContent>
            <CardContent>
              <Typography
                style={{
                  fontFamily: "Times New Roman",
                  color: "black",
                  fontSize: 10,
                  marginLeft: 10,
                  marginTop: -45
                }}
              >
                {this.state.PRICE}
              </Typography>

            </CardContent>

          </CardActionArea>
          <CardActions className="bookdiv">
            <div className="button1" onClick = {this.AddtoBag}>
              <Button
                style={{
                  border: "none",
                  color: "black",
                  textDecoration: "none",
                  display: "inlineBlock",
                  transitionDuration: 0.4,
                  marginTop:-70,marginTop:-70,
                  width: 80,
                  height: 30,
                  fontSize: "0.60em"
                }}
              >
                Add to Bag
              </Button>
            </div>
            <div className="button2" onClick={this.AddWishList}>
              <Button
                style={{
                  width: 70,
                  height: 30,
                  marginTop:-70,
                  fontFamily: "Times New Roman",
                  color: "black",
                  fontSize: "0.60em"
                }} >
                Wishlist
              </Button>
            </div>

          </CardActions>

        </Card>

      </div>
    );
  }
}

export default dashboard;
