import React, { Component } from "react";
require("./unittype");
var unit = require("../src/unit.json");
var APIcall = require("./unitConverCallAPI");

// var keys = Object.keys(unit);
// var unitkey;
// var unitfield;

class secoundInput extends Component {
  constructor(props) {
    super(props);
    this.state = {
      type: props.unit,
      firstUnit: "Enter Value",
      secoundUnit: "Enter Value",
      value: 0,
      result: 0,
      unitsss: []
    };
  }
  componentWillReceiveProps(props) {
    // this.setState({ type: props.unit });
    this.setState({ unitsss: props.typeunit });
  }

  getfirstUnit = event => {
    this.setState({ firstUnit: event.target.value });
  };

  // getUnit = async event => {
  //   await this.setState({ unit: event.target.value });
  //   console.log("unittype========>", this.state.unit);
  //   var unitType = {
  //     type1: this.state.unit
  //   };
  //   console.log("unitype onject------->", unitType);
  //   APIcall.getunits(unitType).then(res => {
  //     console.log(res);
  //   });
  // };

  getsecoundUnit = async event => {
    await this.setState({ secoundUnit: event.target.value });

    var unitObject = {
      type1: this.state.type,
      firstUnit: this.state.firstUnit,
      secoundUnit: this.state.secoundUnit,
      value: this.state.value
    };
    APIcall.getValueConverted(unitObject).then(res => {
      this.setState({ result: res.data.data });
    });
  };

  getValue = async event => {
    await this.setState({ value: event.target.value });
    // console.log("datataatatt-->",this.props.typeunit);
    // var unitssss = this.props.typeunit
    // var a = JSON.stringify(unitssss)
    // console.log(unitssss[0]);
    // this.setState({unitsss : this.props.units})
    // console.log("inputUnits Calss------------>",this.state.units);

    // var unitObject1 = {
    //   type1: this.state.type
    // }
    // console.log("unitype onject------->", unitObject1);
    // APIcall.getunits(unitObject1).then(res => {
    //   this.setState({ units: res.data.data });
    // });
    // console.log("asdfg",this.state.units[0]);
  };

  render() {
     const unitfield = this.state.unitsss.map((value,key)=>{
        return(
          <option key={key}>{value}</option>
        )
     })
    return (
      <div>
        <div>
          <label id="userInput"> Enter Value :</label>
          <input
            value={this.state.value}
            onChange={this.getValue}
            pattern="[0-9]"
            title="Enther Only Numeric Value"
            type="text"
            id="output"
          />

          <select
            id="firstUnit"
            value={this.state.firstUnit}
            onChange={this.getfirstUnit}
          >
            <option value="N/A">Select Unit </option>
            {unitfield}
          </select>
        </div>
        <div>
          <label id="userInput"> Enter Value :</label>
          <input
            value={this.state.value}
            onChange={this.getValue}
            pattern="[0-9]"
            title="Enther Only Numeric Value"
            type="text"
            id="output"
            value={this.state.result}
          />
          <select
            id="secoundUnit"
            value={this.state.secoundUnit}
            onChange={this.getsecoundUnit}
          >
            <option value="N/A">Select Unit </option>
            {unitfield}
          </select>
        </div>
      </div>
    );
  }
}

export default secoundInput;
