import React, { Component } from "react";
// import { Link } from "react-router-dom";
var APIcall = require('../BookStoreCallAPI')


class SignUpForm extends Component {
  constructor() {
    super();

    this.state = {
      TITLE: "",
      AUTHOR: "",
      YEAR: "",
      RATING: "",
      PRICE: "",
      DESCRIPTION: ""
    };

    // this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    let target = e.target;
  }

  handleSubmit(e) {}

  gettitle = event =>{
    this.setState({ TITLE: event.target.value });
  }
  getauthor = event =>{
    this.setState({ AUTHOR: event.target.value });
  }
  getyear = event =>{
    this.setState({ YEAR: event.target.value });
  }
  getrating = event =>{
    this.setState({ RATING: event.target.value });
  }
  getprice = event =>{
    this.setState({ PRICE: event.target.value });
  }
  getdescription = event =>{
    this.setState({ DESCRIPTION: event.target.value });
  }
  getbookdetails = event => {
    var bookDetails = {
      TITLE: this.state.TITLE,
      AUTHOR: this.state.AUTHOR,
      YEAR: this.state.YEAR,
      RATING: this.state.RATING,
      PRICE: this.state.PRICE,
      DESCRIPTION: this.state.DESCRIPTION
    }

    APIcall.getBookDetails(bookDetails)
    .then(res =>{
      console.log(res.data);
    })
  }

  render() {
    console.log(this.state.PRICE);

    return (
      <div className="FormCenter">
        <form className="FormFields">
        <div className="FormField">
            <label className="FormField__Label" htmlFor="Title">
              Title
            </label>
            <input
              value={this.state.value}
              onChange = {this.gettitle}
              type="title"
              id=""
              className="FormField__Input"
              placeholder="Enter book title name"
              name="title"
              onChange={this.handleChange}
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label" htmlFor="Author">
              Author
            </label>
            <input
               value={this.state.value}
               onChange = {this.getauthor}
              type="Author"
              id=""
              className="FormField__Input"
              placeholder="Enter book author name"
              name="author"
              onChange={this.handleChange}
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label" htmlFor="Year">
              Year
            </label>
            <input
             value={this.state.value}
             onChange = {this.getyear}
              type="Year"
              id=""
              className="FormField__Input"
              placeholder="Enter book published year"
              name="year"
              onChange={this.handleChange}
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label" htmlFor="Price">
              Price
            </label>
            <input
             value={this.state.value}
             onChange = {this.getprice}
              type="Price"
              id=""
              className="FormField__Input"
              placeholder="Enter book price"
              name="price"
              onChange={this.handleChange}
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label" htmlFor="Rating">
              Rating
            </label>
            <input
             value={this.state.value}
             onChange = {this.getrating}
              type="rating"
              id="email"
              className="FormField__Input"
              placeholder="Rating of book"
              name="rating"
              onChange={this.handleChange}
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label" htmlFor="description">
              Description
            </label>
            <input
             value={this.state.value}
             onChange = {this.getdescription}
              type="Description"
              id="email"
              className="FormField__Input"
              placeholder="Enter book description"
              name="descrition"
              onChange={this.handleChange}
            />
          </div>
          <div className="FormField">
            <button
            // value={this.state.value}
            onChange = {this.getbookdetails}
              className="FormField__Button mr-20"
              onSubmit={this.handleSubmit}
            >
              Submit
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default SignUpForm;
