import React,{ Component } from "react";
import ListOfBooks from '../../component/dashboard/listOfBooks'  
import Pagination from '@material-ui/lab/Pagination';
var APIcall = require('../../congfiguration/BookStoreCallAPI') 
class BookStoreFirstPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            page : 1,
          ALLBOOKS: [],
          noOfRecord:20
        };
      }

      allBooks() {
        APIcall.getAllBooks({page: this.state.page}).then(res => {
        //   console.log("in get all books---------->",res.data);
          this.setState({ ALLBOOKS: res.data });
        });
      }

      getNoCount(){
          APIcall.getNoOFBookCount().then(res => {
            console.log("in get no of count books---------->",res.data);
            this.setState({ ALLBOOKS: res.data });
          });
      }
      componentWillMount(){
          this.allBooks()
          this.getNoCount()
      }

      handleChange = async(event, value) =>{
            await this.setState({ page : value})
            this.allBooks()
      }

      render(){
          return(
            <ListOfBooks bookList = {this.state.ALLBOOKS} handleChange = {this.handleChange}noOfRecord={this.state.noOfRecord}/>
          )
      }

}
export default BookStoreFirstPage
