import React, { Component } from "react";
require("./unittype");
var unit = require("../src/unit.json");
var APIcall = require('./unitConverCallAPI') 

var keys = Object.keys(unit);
var unitkey;

class secoundInput extends Component {
  constructor(props) {
    super(props);
    this.state = {
       type: props.unit,
       firstUnit : "FEET",
       secoundUnit : "FEET",
       value1 : 0,
       value2 : 0,
       result: 0
     };
  }
  componentWillReceiveProps(props) {
    this.setState({ type: props.unit });
  }

  getfirstUnit = event => {
    this.setState({ firstUnit: event.target.value });
  };

  getsecoundUnit = async (event) => {
  await this.setState({ secoundUnit: event.target.value });

  var unitObject = {}
  if(this.state.value1 == 0){
    unitObject = {
       type1: this.state.type,
       firstUnit : this.state.firstUnit,
       secoundUnit : this.state.secoundUnit,
       value : this.state.value,
       value2 : this.state.value
    }
  }else{
    unitObject = {
      type1: this.state.type,
      firstUnit : this.state.firstUnit,
      secoundUnit : this.state.secoundUnit,
      value1 : this.state.value,
      value2 : this.state.value
   }
  };

  var unitObject = {
      // firstValue = this.state.value 
        type1: this.state.type,
       firstUnit : this.state.firstUnit,
       secoundUnit : this.state.secoundUnit,
       value1 : this.state.value,
       value2 : this.state.value

    }
    APIcall.getValueConverted(unitObject).then(res => {
      this.setState({result: res.data.data})
  })

  };

  get1Value = event => {
    this.setState({ value: event.target.value });
  };

  get2Value = event => {
    this.setState({ value: event.target.value });
  };
  render() {
    var type = this.state.type;
    var a = " ";

    for (var i = 0; i <= keys.length; i++) {
      if (type == i) {
        unitkey = Object.keys(unit[keys[i]]);
      }
    }

    return (
      <div>
        <div>
        <label id="userInput" > Enter Value :</label>
        <input
          value ={this.state.value}  
          onChange={this.get1Value}
          pattern="[0-9]"
          title="Enther Only Numeric Value"
          type="text"
          id="output"
        />

        <select id="firstUnit" value ={this.state.firstUnit} onChange={this.getfirstUnit} >
          <option value="N/A">Select Unit </option>
          <option>{unitkey[0] || a} </option>
          <option>{unitkey[1] || a}</option>
          <option>{unitkey[2] || a}</option>
          <option>{unitkey[3] || a}</option>
        </select>
        </div>
        <div>
        <label id="userInput"> Enter Value :</label>
        <input
        value ={this.state.value}  
        onChange={this.get2Value}
          pattern="[0-9]"
          title="Enther Only Numeric Value"
          type="text"
          id="output"
          value={this.state.result}
        />
        <select id="secoundUnit" value = {this.state.secoundUnit}  onChange={this.getsecoundUnit}>
          <option value="N/A">Select Unit </option>
          <option>{unitkey[0] || a} </option>
          <option>{unitkey[1] || a}</option>
          <option>{unitkey[2] || a}</option>
          <option>{unitkey[3] || a}</option>
        </select>
        </div>
      </div>
    );
  }
}

export default secoundInput;
