      console.log("compountWillMount",res.data.type);
        this.setState({ 
          type :res.data.type, 
      },()=> console.log("unittypeeeeeeeeeeeeeeee", this.state.type))
    })

    }
  render() {
      console.log("in render----=>",this.state.type);
