import React, { Component } from "react";
var APIcall = require('../../BookStoreCallAPI')


class SignUpForm extends Component {
  constructor() {
    super();
    this.state = {
      TITLE: "",
      AUTHOR: "",
      YEAR: "",
      RATING: "",
      PRICE: 89,
      DESCRIPTION: "",
      FILE  : "",
      IMAGEPATH:""
    };
  }

  gettitle = event =>{
    this.setState({ TITLE: event.target.value });
  }
  getauthor = event =>{
    this.setState({ AUTHOR: event.target.value });
  }
  getyear = event =>{
    this.setState({ YEAR: event.target.value });
  }
  getrating = event =>{
    this.setState({ RATING: event.target.value });
  }
  getprice = event =>{
    this.setState({ PRICE: event.target.value });
    console.log(this.state.PRICE);

  }
  getdescription = event =>{
    this.setState({ DESCRIPTION: event.target.value });
  }
  getfile = async event => {
    console.log("select--> ",event.target.files[0])
    this.setState({ FILE: event.target.files[0] });
    const filePath = {
      FILE_PATH : await this.state.FILE,
    }
    event.preventDefault();
        const formData = new FormData();
        formData.append('filePath',this.state.FILE);
    APIcall.getImagePath(formData)
    .then(res =>{
      console.log("res in file upload--> ",res.data);
      this.setState({ IMAGEPATH: res.data});
    })

  }
  getfilepath =  () => {
    console.log("in file Path",this.state.FILE);
  }
  getbookdetails = () => {
    console.log("call to submit");

     const bookDetails = {
      TITLE: this.state.TITLE,
      AUTHOR: this.state.AUTHOR,
      YEAR: this.state.YEAR,
      RATING: this.state.RATING,
      PRICE: this.state.PRICE,
      DESCRIPTION: this.state.DESCRIPTION,
      IMAGEPATH : this.state.IMAGEPATH
    }

    console.log(bookDetails);

    APIcall.getBookDetails(bookDetails)
    .then((res) =>{
      console.log(res.data);
    }).catch((err) => {
      console.log("err while submitting--> ", err)
    })
  }


  render() {
    // console.log("ASDFGHJKL",this.state.FILE);

    return (
      <div className="FormCenter">
        <form className="FormFields">
        <div className="FormField">
            <label className="FormField__Label">
              Title
            </label>
            <input
              value={this.state.value}
              onChange = {this.gettitle}
              type="title"
              className="FormField__Input"
              placeholder="Enter book title name"
              name="title"
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label">
              Author
            </label>
            <input
               value={this.state.value}
               onChange = {this.getauthor}
              type="Author"
              className="FormField__Input"
              placeholder="Enter book author name"
              name="author"
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label" >
              Year
            </label>
            <input
             value={this.state.value}
             onChange = {this.getyear}
              type="Year"
              className="FormField__Input"
              placeholder="Enter book published year"
              name="year"
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label">
              Price
            </label>
            <input
             value={this.state.value}
             onChange = {this.getprice}
              type="Price"
              className="FormField__Input"
              placeholder="Enter book price"
              name="price"
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label">
              Rating
            </label>
            <input
             value={this.state.value}
             onChange = {this.getrating}
              type="rating"
              className="FormField__Input"
              placeholder="Rating of book"
              name="rating"
            />
          </div>

          <div className="FormField">
            <label className="FormField__Label">
              Description
            </label>
            <input
             value={this.state.value}
             onChange = {this.getdescription}
              type="Description"
              className="FormField__Input"
              placeholder="Enter book description"
              name="descrition"
            />
          </div>
          <div className="FormField">
            <input accept = "image/*" type='file' value={this.state.value} onChange ={this.getfile}/>
            <button onClick={this.getfilepath} style = {{height:21,width:87}} >Upload</button>
          </div>
          </form>

          <div className="FormField">
            <button
            // value={this.state.value}
            // onChange = {this.getbookdetails}
               type ='submit'
              className="FormField__Button mr-20"
              onClick={this.getbookdetails}
            >
              Submit
            </button>
          </div>
      </div>
    );
  }
}

export default SignUpForm;
