}

render(){
  return (
    <div className = "dropdown">
    <PopupState variant="popover" popupId="demo-popup-menu">
      {popupState => (
        <React.Fragment>

          <Button {...bindTrigger(popupState)} style={{ fontFamily:"Times New Roman", fontSize:10,borderStyle: "ridge",borderWidth: "thin",borderColor:"grey"}}>
            Sort By Relevance
          </Button>
          <Menu {...bindMenu(popupState)}>
            <MenuItem style = {{fontFamily:"Times New Roman", fontSize:12}} onClick={this.highToLowClick}  >High-Low</MenuItem>
            <MenuItem style = {{fontFamily:"Times New Roman", fontSize:12}}  onClick={this.lowToHighClick}>Low-High</MenuItem>
            <MenuItem style = {{fontFamily:"Times New Roman", fontSize:12}}  onClick={this.sortArrivalClick}>Newest Arrival</MenuItem>
          </Menu>

        </React.Fragment>
      )}
    </PopupState>
    </div>
  );
}
}
