console.log("cachehits is not empty------------------->");
this.setState({hits: JSON.parse(cacheHits)});
}
else{
fetch('http://localhost:3001/addToCart' + query)
.then(response=> response.json())
.then(result => this.onSetResult(result,query));
}
}

onSetResult=(result,key)=>{
