import React, { Component } from "react";
import Dashboard from "../dashboard/dashboard";
import Footer from "../footer/footer";
import Pagination from '@material-ui/lab/Pagination';
var APIcall = require("../../congfiguration/BookStoreCallAPI");

class dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      addToCart : new Map()
    }
  }

  getbook = (value) => {
    // console.log("in list of book calss",value);
    // this.setState({ addToCart:value });
    // this.props.getBookToaddToCart(this.state.addToCart)
    this.state.addToCart.set(value.Title,true)
    this.setState({addToCart : this.state.addToCart})
    this.props.getBookToaddToCart(value)
  }
  addToCart = {

  }
  render() {
    console.log("in list of books-------> ");

    return (
      <div>
        <div basename="/react-auth-ui/">
          <div className="App__Form">
            <div style={{ display: "flex", flexWrap: "wrap",marginLeft:"20px",marginLeft:"100px",marginTop:"-40px"}}>
                  {this.props.bookList.map((value, index) => {
                return <Dashboard key={index} value={value} getBook = {this.getbook}></Dashboard>;
              })}
            </div>

          </div>
          <Pagination shape = "rounded" style={{display:"flex",justifyContent:"center",marginTop:"30px",marginBottom:"20px"}} count={Math.ceil(this.props.noOfRecord / 12)}
            onChange = {this.props.handleChange}/>
          <Footer></Footer>
        </div>
      </div>
    );
  }
}
export default dashboard;
