import React, { Component } from "react";
import Fisrtunit from "./inputunit";
import Secoundunit from "./inputunit";
// var APIcall = require("./unitConverCallAPI");

export default class unittype extends Component {
  state = {
    unit: ""
  };
  getUnit = async event => {
    await this.setState({ unit: event.target.value });
    // console.log("unittype========>", this.state.unit);
    // var unitType = {
    //   type1: this.state.unit
    // };
    // console.log("unitype onject------->", unitType);
    // APIcall.getunits(unitType).then(res => {
    //   console.log(res);
    // });

    var unitObject1 = {
      type1: this.state.type
    }
    console.log("unitype onject------->", unitObject1);
    APIcall.getunits(unitObject1).then(res => {
      this.setState({ units: res.data.data });
    });
    console.log("asdfg",this.state.units[0]);

  };
  render() {
    return (
      <div>
        <select id="SelectUnit" value={this.state.unit} onChange={this.getUnit}>
          <option value="N/A">Select Type </option>
          <option value="0">LENGTH</option>
          <option value="1">VOLUME</option>
          <option value="2">MASS</option>
          <option value="3">TEMPERATURE</option>
        </select>
        {/* <Secoundunit unit={this.state.unit} /> */}
        <Secoundunit unit={this.state.unit} />
        <Fisrtunit unit={this.state.unit} />
      </div>
    );
  }
}
