import React, { Component } from "react";
require("../src/unittype");
var unit = require("../src/unit.json");

var keys = Object.keys(unit);
var unitkey = Object.keys(unit[keys[0]]);

class secoundInput extends Component {
  constructor(props) {
    super(props);
    this.state = { type: props.unit };
  }
  componentWillReceiveProps(props) {
    this.setState({ type: props.unit });
  }
  render() {
    console.log("in method--->", this.state.type);
    var type = this.state.type;
    console.log("type variable------->", type);

    console.log(keys.length);
    var a = "";
    for (var i = 0; i <= keys.length; i++) {
      if (type == i) {
        keys = Object.keys(unit);
        unitkey = Object.keys(unit[keys[i]]);
      }
    }
    console.log("type variable------->", unitkey);

    return (
      <div>
        <label htmlFor="fname">Enter Value :</label>
        <input
          type="text"
          id="output"
          // onChange={this.changeFirstInput}
          // value={this.state.firstInput}{this.setState()}
        />

        <select id="secoundUnit">
          <option value="N/A">Select Unit </option>
          <option>{unitkey[0] || a} </option>
          <option>{unitkey[1] || a}</option>
          <option>{unitkey[2] || a}</option>
          <option>{unitkey[3] || a}</option>
        </select>
      </div>
    );
  }
}

export default secoundInput;
