import {renderToContext, itTransitionsOn, itDoesNotTransitionOn} from '../util'


describe("default component:", function() {
  beforeEach(function() {
    renderToContext(this)
  })

  itTransitionsOn('running cancel behavior', function() {
    this.component.__cancelDown()
  }, {
    from: {selecting: true, hover: true, acting: true, beacon: true},
    to: {selecting: false, hover: false, acting: false, beacon: false},
  })
})
